import React from "react";
import { Component } from "react";
import { useState } from "react";
import actions from "../actions/actions";


class Homepage extends Component{
    constructor(props){
        super(props)
        this.state = {
            username : props.username,
            courses : '',
        }
        this.sign = this.handleSign.bind(this)
        this.createCourse = this.handleCreateCourse.bind(this)
        this.test = this.handleTest.bind(this)
        this.getCourses = this.handleGetCourses.bind(this)
    }
    handleTest(e){
        actions.getCourseRooms({number: "09876"})
    }

    async handleGetCourses(e){
        try{
            let _courses = ''
            const response = await actions.getCourses()
            response.forEach(element => {
                _courses += `Course number: ${element.number}\t\tCourse name: ${element.name}\n`
                console.log(_courses)
            });
            this.setState({courses: _courses})

        }catch(err){
        }
    }
    
    handleCreateCourse(e){
        actions.createCourse({number: "09876",name: "Ariel root sanda"})
    }

    handleSign(e){
        actions.sign("...") //TODO - Get course number from form
    }

    render(){
        return(
            <div className="hero">
              <nav>
              <h2 className="logo">Moodle<span>STU</span></h2>
              <label id="name">Name: {this.state.username} </label>
                    <ul>
                        <label><li className="link"onClabelck={this.sign}>Sign to course</li></label>&emsp;&emsp;
                        <label><li className="link" onClabelck={this.createCourse}>Create course</li></label>&emsp;&emsp;
                        <label><li className="link" onClabelck={this.test}>Self destruct</li></label>&emsp;&emsp;
                        <label><li className="link" onClabelck={(this.getCourses)}>My courses</li></label>&emsp;&emsp;
                    </ul>
              </nav>
              <div>
              <div className="content">
                        <ul id="courseslabelst">
                        </ul>
                        <ul>
                            <label id="adfadf">Courses: {this.state.courses} </label>
                        </ul>
                    </div>
              </div>
            </div>
        )
    }
}



export default Homepage
