import React, { createContext } from "react";
import { Component } from "react";
import api from "../services/api";

class Homepage extends Component{
    constructor(props){
        super(props)
        this.state = {
        }
        this.sign = this.handleSign.bind(this)
        this.createCourse = this.handleCreateCourse.bind(this)
    }

    handleCreateCourse(e){
        console.log("Create")
    }

    async handleSign(e){
        try{
            const data = await api.call('get','',{})
        }catch(err)
        {

        }
        console.log(data)
    }

    render(){
        return(
            <div class="hero">
            <head>
            <title>STU Moodle</title>
            </head>
              <nav>
              <h2 class="logo">Moodle<span>STU</span></h2>
                    <ul>
                        <li><button onClick={this.sign}>Sign to course</button></li>
                        <li><button onClick={this.createCourse}>Create course</button></li>
                    </ul>
              </nav>
              <div>
              <div class="content">
                        <h2 class="Student_details"> Student details:</h2>
                        <ul id="coursesList">
                            <li id="name">Name: </li>
                            <li id="id">ID: </li>
                        </ul>
                    </div>
                    <div class="courses">
                        <h2 class="courses_list"> Courses List:</h2>
                        <ul id="coursesList"></ul>
                    </div>
              </div>
            </div>
        )
    }
}

export default Homepage
