import React from "react"
import {connect} from 'react-redux'
import {vote} from '../store/actions/polls'

const Poll = ({poll, auth, vote}) => {
    const answers = poll.options && poll.options.map(option => (
        <button onClick={() => vote(poll._id, {answer: option.option})} key={option._id}>{option.option}</button>
    ))
    if(auth.user.id === poll.user._id)
    {
        console.log(poll)
        const data = {
            label: poll.options.map(option => option.option),
            label: poll.options.option.vote
        }
    }

    return (
        <div>
            <h3>{poll.question}</h3>
            <div>{answers}</div> 
        </div>
    )
}

export default connect(store => ({poll: store.currentPoll, auth: store.auth}), {vote})(Poll)
