import api from "../services/api"

export const getCourses = async () => {
    try{
        const response = await api.call('get','course')
        return response
    }catch (err){
        console.log(err)
    }   
}

export const sign = async (courseNumber) => {
    console.log("hello from sign")
    try{
        const response = await api.call('post','course/user',{courseNumber: courseNumber})
        console.log(response)
        return response
    }catch (err){
        console.log(err)
    }
}

export default {getCourses, sign}
