import {SET_POLLS,SET_CURRENT_POLL} from '../actionsTypes'

const polls = (state = [], action) => {
    switch (action.type){
        case SET_POLLS:
            return action.polls
        default:
            return state
    }

}

const currentPoll = (state = {}, action) => {
    switch (action.type){
        case SET_CURRENT_POLL:
            return action.poll
        default:
            return state
    }

}

export default {polls, currentPoll}
