import React from "react"
import { useState } from "react";
import {Provider} from 'react-redux'
import {store} from '../store'
import {setCurrentUser, setToken, removeError} from '../store/actions'
import decode from 'jwt-decode'
import {BrowserRouter as Router} from 'react-router-dom'
import RouteViews from './RoutesViews'
import NavBar from './NavBar'
import ErrorMessage from "../components/ErrorMessage"

var createGuest = require("cross-domain-storage/guest");
var createHost = require("cross-domain-storage/host");
var storageHost = createHost([
  {
    origin: "http://localhost:4000",
    allowedMethods: ["get", "set", "remove"], 
  },
  {
    origin: "http://localhost:5000",
    allowedMethods: ["get", "set", "remove"], 
  },
]);//<--- First add yourself then others, you MUST add yourself, this tells us who is allowed to reach us and with what methods

var remoteStorage = createGuest("http://localhost:5000"); // <--- Who we want to be guests of

//When turning on app, it will check if user already logged in
if(localStorage.jwtToken){
    setToken(localStorage.jwtToken)
    try{
        store.dispatch(setCurrentUser(decode(localStorage.jwtToken)))
    }catch(err){
        store.dispatch(setCurrentUser({}))
        store.dispatch(removeError({}))
    }
}

const App = () => {
const [localValue, setLocalValue] = useState("");
const [crossDomainValue, setCrossDomainValue] = useState('')
return (<Provider store = {store}>
    <Router>
        <div>
        <header className="App-header">
        <p>Welcome to Cross Domain Storage Access Demo</p>
        <input
          placeholder="Enter your value"
          onChange={(e) => {
            setLocalValue(e.target.value);
          }}
          value={localValue}
        ></input>
        <button
          onClick={(e) => {
            e.preventDefault();
            localStorage.setItem("localStorageKey", localValue);
            setLocalValue("");
          }}
        >
          Save to Storage
        </button>
        <button
          onClick={(e) => {
            e.preventDefault();
            remoteStorage.get("localStorageKey", function (error, value) {
              // value for the key of 'fizz' will be retrieved from localStorage on www.baz.com
              if(error){
                console.log(error)
              }else{
                setCrossDomainValue(value)
              }
            });
          }}
        >
          Access from Cross Domain Storage
        </button>
        <p>
          Value stored in current Domain Storage:{" "}
          {localStorage.getItem("localStorageKey")}
        </p>
        <p>
          Value stored in cross Domain Storage:{" "}
          {crossDomainValue}
        </p>
      </header>
            <NavBar/>
            <RouteViews />
            <ErrorMessage/>
        </div>
    </Router>
</Provider>)
}

export default App
