import React, {Component} from "react";
import {connect} from 'react-redux'
import {getPolls,getUserPolls} from '../store/actions/polls'
import polls from "../store/reducers/polls";


class Polls extends Component{

    constructor(props){
        super(props)
    }

    componentDidMount(){
        const {getPolls} = this.props
        getPolls()
        const polls = this.props.polls.map(poll => (<li>{poll.question}</li>))
    }

    render(){
        return 
        <div>
            <ul>{polls}</ul>
        </div>
    }
}

export default connect(store => ({auth: store.auth, polls: store.polls}),{getPolls, getUserPolls})(Polls)
