import React, {Component} from "react";
import {connect} from 'react-redux'
import {getPolls,getUserPolls, getCurrentPoll} from '../store/actions/polls'
import {polls} from "../store/reducers/polls";


class Polls extends Component{

    constructor(props){
        super(props)
    }

    componentDidMount(){
        const {getPolls} = this.props
        getPolls()
    }

    handleSelect(id) {
        const {getCurrentPoll} = this.props
        getCurrentPoll(id)
    }

    render(){
        return (
            <div>
                <ul>{this.props.polls.map(poll => (<li key={poll._id} onClick={() => this.handleSelect(poll._id)}>{poll.question}</li>))}</ul>
            </div>
        )
    }
}

export default connect(store => ({auth: store.auth, polls: store.polls}),{getPolls, getUserPolls, getCurrentPoll})(Polls)
