import {SET_POLLS,SET_CURRENT_POLL} from '../actionsTypes'

const pollsReducer = (state = [], action) => {
    switch (action.type){
        case SET_POLLS:
            return action.polls
        default:
            return state
    }

}

const currentPollReducer = (state = {}, action) => {
    switch (action.type){
        case SET_CURRENT_POLL:
            return action.poll
        default:
            return state
    }

}

export default {pollsReducer, currentPollReducer}
