import {ADD_ERROR,REMOVE_ERROR} from '../actionsTypes'

export default (state = {message: null}, action) =>{
    console.log(action)
    switch(action.type){
        case ADD_ERROR:
            return {...state, message: action.error}

        case REMOVE_ERROR:
            return {...state, message: null}

        default:
            return state
    }
}
